// Home.js
import React from 'react';


const Home = () => {
  return (
    <div>
      
      <div style={styles.homeContent}>
        <h2>Welcome to The Online Banking Application</h2>
        {/* Add additional content */}
      </div>
    </div>
  );
};

const Card = ({ id, content, isPulled, onPull }) => {
  return (
    <div
      className={`card ${isPulled ? 'pulled' : ''}`}
      onClick={() => onPull(id)}
    >
      {content}
    </div>
  );
};

const ClickToPullCards = () => {
  const [cards, setCards] = useState([
    { id: 1, content: 'Card 1', isPulled: false },
    { id: 2, content: 'Card 2', isPulled: false },
    { id: 3, content: 'Card 3', isPulled: false },
  ]);

  const handlePullCard = (cardId) => {
    setCards((prevCards) =>
      prevCards.map((card) =>
        card.id === cardId ? { ...card, isPulled: !card.isPulled } : card
      )
    );
  };

  return (
    <div className="card-container">
      {cards.map((card) => (
        <Card
          key={card.id}
          id={card.id}
          content={card.content}
          isPulled={card.isPulled}
          onPull={handlePullCard}
        />
      ))}
    </div>
  );
};
const styles = {
  homeContent: {
    padding: '20px',
    marginTop: '50px', // Adjust as needed
  },
};

export default Home;
